<script>
export default {
  name: 'horizontal-list',
  functional: true,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
  render: (h, { children, data }) => {
    const list = [];
    const addVerticalLine = (item) => {
      if (item.tag === undefined) return;
      list.push(item);
      list.push(<Span class="h-line" />);
    };
    children.forEach(addVerticalLine);
    list.pop();

    return <div class="h-list" {...data}>{list}</div>;
  }
}
</script>